import React from 'react';

type Props = {
  onClick: () => void;
  disabled?: boolean;
  loading?: boolean;
};

const TranslateButton: React.FC<Props> = ({ onClick, disabled, loading }) => (
  <button
    onClick={onClick}
    disabled={disabled}
    style={{
      width: '100%',
      padding: '0.8rem',
      borderRadius: '12px',
      border: 'none',
      background: disabled ? '#ccc' : 'linear-gradient(45deg, #6a11cb, #2575fc)',
      color: 'white',
      fontWeight: 'bold',
      cursor: disabled ? 'not-allowed' : 'pointer',
      marginBottom: '1rem',
    }}
  >
    {loading ? '翻訳中...' : '翻訳'}
  </button>
);

export default TranslateButton;
