import React from 'react';

type Language = {
  code: string;
  label: string;
};

type Props = {
  languages: Language[];
  value: string;
  onChange: (value: string) => void;
  exclude?: string;
};

const LanguageSelect: React.FC<Props> = ({ languages, value, onChange, exclude }) => {
  return (
    <select
      value={value}
      onChange={(e) => onChange(e.target.value)}
      style={{
        flex: 1,
        padding: '0.8rem',
        borderRadius: '12px',
        border: '1px solid #ccc',
        fontSize: '1rem',
      }}
    >
      {languages
        .filter((l) => l.code !== exclude)
        .map((l) => (
          <option key={l.code} value={l.code}>
            {l.label}
          </option>
        ))}
    </select>
  );
};

export default LanguageSelect;
